import React from "react";
import { useEffect, useState } from "react";
import {
  connectWallet,
  getCurrentWalletConnected,
  sellTicket,
  getAllPartecipants,
  GetLastTicketSold,
  getNumOfPartecipants,
  extractLottery,
  ShowWinner,
} from "./util/interact.js";
let ListPartecipants;
const buttonStyle = {
  padding: "10px 15px",
  backgroundColor: "#3498db",
  color: "#fff",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  margin: "0 10px", // Adjust the margin to add space between buttons
};
const inputStyle = {
  width: "200px",
  padding: "8px",
  border: "1px solid #3498db",
  borderRadius: "5px",
};

const Lottery = () => {
  const [walletAddress, setWallet] = useState("");
  const [status, setStatus] = useState(""); //default message
  const [message, setMessage] = useState("");
  const [newMessage, setNewMessage] = useState("");
  const [Name, setName] = useState(""); //default message
  const [Surname, setSurname] = useState(""); //default message
  const [Id, setId] = useState(""); //default message
  const [Ticket, setTicket] = useState("1"); //default message
  const [resultTransaction, setStatusTransaction] = useState("");
  const [Partecipant, setPartecipant] = useState("");
  const [LastPartecipant, setLastPartecipant] = useState("");
  const [NumPartecipant, setNumPartecipant] = useState("");
  const [Winner, setWinner] = useState("");

  //called only once
  useEffect(() => {
    // get the connected wallet
    async function getWalletConnected() {
      const { address, status } = await getCurrentWalletConnected();
      setWallet(address);
      setStatus(status);
    }
    getWalletConnected();
    walletListener();
  }, []);
  const getAllPatecipants = async () => {
    //TODO: implement
    if (window.ethereum.selectedAddress == null) {
      window.alert("Fai Login con Metamask");
    } else {
      const { status, Partecipant } = await getAllPartecipants(walletAddress);
      setPartecipant(Partecipant);
      if (status) setStatus(status);
      if (Array.isArray(Partecipant)) {
        ListPartecipants = Partecipant.map((Partecipant) => (
          <li>{Partecipant}</li>
        ));
      } else {
        ListPartecipants = <li>{Partecipant}</li>;
      }
    }
  };

  const LastTicketSold = async () => {
    //TODO: implement
    if (window.ethereum.selectedAddress == null) {
      window.alert("Fai Login con Metamask");
    } else {
      const { status, LastPartecipant } = await GetLastTicketSold(
        walletAddress
      );
      setLastPartecipant(LastPartecipant);
      if (status) setStatus(status);
    }
  };

  const NumberOfPartecipants = async () => {
    //TODO: implement
    if (window.ethereum.selectedAddress == null) {
      window.alert("Fai Login con Metamask");
    } else {
      const { status, NumPartecipant } = await getNumOfPartecipants(
        walletAddress
      );
      setNumPartecipant(NumPartecipant);
      if (status) setStatus(status);
    }
  };

  function walletListener() {
    if (window.ethereum) {
      window.ethereum.on("accountsChanged", (accounts) => {
        if (accounts.length > 0) {
          setWallet(accounts[0]);
        } else {
          setWallet("");
          setStatus(
            'ðŸ¦Š Connect to Metamask using the "Connect Wallet" button.'
          );
        }
      });
    } else {
      setStatus(
        <p>
          {" "}
          ðŸ¦Š{" "}
          <a target="_blank" href={`https://metamask.io/download.html`}>
            You must install Metamask in your browser.
          </a>
        </p>
      );
    }
  }

  const connectWalletPressed = async () => {
    const walletResponse = await connectWallet();
    setStatus(walletResponse.status);
    setWallet(walletResponse.address);
  };

  const buyTicket = async (e) => {
    e.preventDefault();
    const { resultTransaction } = await sellTicket(
      walletAddress,
      Name,
      Surname,
      Id,
      Ticket
    );
    setStatusTransaction(resultTransaction);
  };
  const Extraction = async () => {
    //TODO: implement
    if (window.ethereum.selectedAddress == null) {
      window.alert("Fai Login con Metamask");
    } else {
      const { status, x } = await extractLottery(walletAddress);
      const { s, Winner } = await ShowWinner(walletAddress);
      setWinner(Winner);
      if (s) setStatus(s);
    }
  };

  const showWinner = async () => {
    //TODO: implement
    if (window.ethereum.selectedAddress == null) {
      window.alert("Fai Login con Metamask");
    } else {
      const { status, Winner } = await ShowWinner(walletAddress);
      setWinner(Winner);
      if (status) setStatus(status);
    }
  };

  return (
    <div id="container" style={{ position: "relative" }}>
      <h1
        style={{
          textAlign: "center",
          color: "#3498db",
          textShadow: "2px 2px 4px #666",
          fontFamily: "Arial, sans-serif",
          fontSize: "2.5em",
          margin: "20px 0",
        }}
      >
        Christmas Lottery of Luca Ferrari
      </h1>
      <button
        id="walletButton"
        onClick={connectWalletPressed}
        style={{
          position: "absolute",
          top: "10px",
          right: "10px",
          padding: "10px 15px",
          backgroundColor: "#3498db",
          color: "#fff",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        {walletAddress.length > 0 ? (
          "Connected: " +
          String(walletAddress).substring(0, 6) +
          "..." +
          String(walletAddress).substring(38)
        ) : (
          <span>Connect Wallet</span>
        )}
      </button>
      <div style={{}}>
        <div style={{ display: "inline-block", margin: "10px" }}>
          <h2>Get all Participants:</h2>
          <button id="lPButton" onClick={getAllPatecipants} style={buttonStyle}>
            Get All Participants
          </button>
          <ul>
            <p>{ListPartecipants}</p>
          </ul>
        </div>
        <div style={{ display: "inline-block", margin: "10px" }}>
          <h2>Last Ticket Sold:</h2>
          <button id="lPButton" onClick={LastTicketSold} style={buttonStyle}>
            Get Last Ticket Sold
          </button>

          <p>{LastPartecipant}</p>
        </div>
        <div style={{ display: "inline-block", margin: "10px" }}>
          <h2>Number of Participants:</h2>
          <button
            id="lPButton"
            onClick={NumberOfPartecipants}
            style={buttonStyle}
          >
            Get The number of Participants
          </button>
          <p>{NumPartecipant}</p>
        </div>
      </div>
      <h2>Buy Ticket:</h2>
      <p>Insert Name:</p>
      <input
        id="name"
        type="text"
        value={Name}
        required
        onChange={(e) => setName(e.target.value)}
      />
      <p>Insert Surname:</p>
      <input
        id="surname"
        type="text"
        value={Surname}
        required
        onChange={(e) => setSurname(e.target.value)}
      />
      <p>Insert ID:</p>
      <input
        id="id"
        type="text"
        value={Id}
        required
        onChange={(e) => setId(e.target.value)}
      />
      <p>Insert Number of Tickets:</p>
      <input
        id="Ticket"
        type="number"
        max={99}
        min={1}
        value={Ticket}
        required
        onChange={(e) => setTicket(e.target.value)}
      />
      <button id="lPButton" onClick={buyTicket}>
        Sell Ticket
      </button>
      <p>{resultTransaction}</p>
      <button id="Extraction" onClick={Extraction}>
        Extract The Winner
      </button>
      <button id="Extraction" onClick={showWinner}>
        Show Winner
      </button>
      <p>{Winner}</p>
    </div>
  );
};

export default Lottery;
