import React from "react";
import { useEffect, useState } from "react";
import {
    connectWallet,
    getCurrentWalletConnected,
  } from "./util/interact.js";

const Lottery = () => {
    const [walletAddress, setWallet] = useState("");
    const [status,setStatus] = useState("");//default message
    const [message,setMessage] = useState("");
    const [newMessage,setNewMessage] = useState("");
    const [Name, setName] = useState(""); //default message
    const [Surname, setSurname] = useState(""); //default message
    const [Ticket, setTicket] = useState("1"); //default message
    const [statusTransaction, setStatusTransaction] = useState("");
    //called only once
    useEffect(() => {
        // get the connected wallet
        async function getConnectedWallet() {
            const { address, status } = await getCurrentWalletConnected();
            setWallet(address);
            setStatus(status);
        }
        getConnectedWallet();
        // event listener for changes in the wallet connection
        walletListener();
    }, []);

    // function addSmartContractListener() {
    //     ChristmasLotteryContract.events.UpdatedMessages({}, (error, data) => {
    //       if (error) {
    //         setStatus("ðŸ˜¥ " + error.message);
    //       } else {
    //         setMessage(data.returnValues[1]);
    //         setNewMessage("");
    //         setStatus("ðŸŽ‰ Your message has been updated!");
    //       }
    //     });
    //   }

    const connectWalletPressed = async () => {
        const walletResponse = await connectWallet();
        setStatus(walletResponse.status);
        setWallet(walletResponse.address);
    };

    const sellTicket = async (walletAddress) => {
        //TODO: implement
    };

    return (
        <div id="container">

            <h1>Lottery</h1>
            <button id="walletButton" onClick={connectWalletPressed}>
        {walletAddress.length > 0 ? (
          "Connected: " +
          String(walletAddress).substring(0, 6) +
          "..." +
          String(walletAddress).substring(38)
        ) : (
          <span>Connect Wallet</span>
        )}
      </button>
            <h2>Buy Ticket:</h2>
            <p>Insert Name:</p>
            <input
                id="name"
                type="text"
                value={Name}
                required
                onChange={(e) => setName(e.target.value)}
            />
            <p>Insert Surname:</p>
            <input
                id="surname"
                type="text"
                value={Surname}
                required
                onChange={(e) => setSurname(e.target.value)}
            />
            <p>Insert Number of Tickets:</p>
            <input
                id="Ticket"
                type="number"
                max={99}
                min={1}
                value={Ticket}
                required
                onChange={(e) => setTicket(e.target.value)}
            />
            <button id="lPButton" onClick={sellTicket}>
                Sell Ticket
            </button>
            <p>{statusTransaction}</p>
        </div>
    );
};

export default Lottery;
