import React from "react";
import { useEffect, useState } from "react";
import {
    connectWallet,
    getCurrentWalletConnected,
    sellTicket,
    getAllPartecipants,
    GetLastTicketSold,
    getNumOfPartecipants,
    extractLottery,
    ShowWinner
  } from "./util/interact.js";
  let ListPartecipants;

const Lottery = () => {
    const [walletAddress, setWallet] = useState("");
    const [status,setStatus] = useState("");//default message
    const [message,setMessage] = useState("");
    const [newMessage,setNewMessage] = useState("");
    const [Name, setName] = useState(""); //default message
    const [Surname, setSurname] = useState(""); //default message
    const [Ticket, setTicket] = useState("1"); //default message
    const [resultTransaction, setStatusTransaction] = useState("");
    const [Partecipant, setPartecipant] = useState("");
    const [LastPartecipant, setLastPartecipant] = useState("");
    const [NumPartecipant, setNumPartecipant] = useState("");
    const [Winner, setWinner] = useState("");


    //called only once
    useEffect(() => {
        // get the connected wallet
        async function getWalletConnected() {
            const { address, status } = await getCurrentWalletConnected();
            setWallet(address);
            setStatus(status);
        }
        getWalletConnected();
        walletListener();
    }, []);
    const getAllPatecipants = async () => { //TODO: implement
        if(window.ethereum.selectedAddress==null){
          window.alert("Fai Login con Metamask")
        }
        else{
          const {status,Partecipant} = await getAllPartecipants(walletAddress);
          setPartecipant(Partecipant);
          if (status) setStatus(status);
          if(Array.isArray(Partecipant)){
            ListPartecipants= Partecipant.map((Partecipant) => (<li>{Partecipant}</li>));
            console.log(ListPartecipants);
          }
          else{
            ListPartecipants= <li>{Partecipant}</li>;
          }
      }
      };

    const LastTicketSold = async () => { //TODO: implement
        if(window.ethereum.selectedAddress==null){
          window.alert("Fai Login con Metamask")}
        else{
          const {status,LastPartecipant} = await GetLastTicketSold(walletAddress);
          setLastPartecipant(LastPartecipant);
          if (status) setStatus(status);
        }
      };

      const NumberOfPartecipants = async () => { //TODO: implement
        if(window.ethereum.selectedAddress==null){
          window.alert("Fai Login con Metamask")}
        else{
          const {status,NumPartecipant} = await getNumOfPartecipants(walletAddress);
          setNumPartecipant(NumPartecipant);
          if (status) setStatus(status);
        }
      };

    function walletListener() {
        if (window.ethereum) {
            window.ethereum.on("accountsChanged", (accounts) => {
                if (accounts.length > 0) {
                    setWallet(accounts[0]);
                } else {
                    setWallet("");
                    setStatus("ðŸ¦Š Connect to Metamask using the \"Connect Wallet\" button.");
                }
            });
        } else {
            setStatus(
                <p>
                    {" "}
                    ðŸ¦Š{" "}
                    <a target="_blank" href={`https://metamask.io/download.html`}>
                        You must install Metamask in your browser.
                    </a>
                </p>
            );
        }
    }


    const connectWalletPressed = async () => {
        const walletResponse = await connectWallet();
        setStatus(walletResponse.status);
        setWallet(walletResponse.address);
    };

    const buyTicket = async (e) => {
        e.preventDefault();
        //TODO: implement
        console.log("Name: " + Name);
        console.log("Surname: " + Surname);
        console.log("Ticket: " + Ticket);
        const { resultTransaction } = await sellTicket(walletAddress,Name,Surname,Ticket);
        setStatusTransaction(resultTransaction);
    };
    const Extraction = async () => { //TODO: implement
        if(window.ethereum.selectedAddress==null){
            window.alert("Fai Login con Metamask")
        }
        else{
            const {status,x} = await extractLottery(walletAddress);
            const {s,Winner} = await ShowWinner(walletAddress);
            setWinner(Winner);
            if (s) setStatus(s);

      }
      };

      const showWinner = async () => { //TODO: implement
        if(window.ethereum.selectedAddress==null){
          window.alert("Fai Login con Metamask")}
        else{
          const {status,Winner} = await ShowWinner(walletAddress);
          setWinner(Winner);
          if (status) setStatus(status);
        }
      };

    return (
        <div id="container">

            <h1>Lottery</h1>
            <button id="walletButton" onClick={connectWalletPressed}>
        {walletAddress.length > 0 ? (
          "Connected: " +
          String(walletAddress).substring(0, 6) +
          "..." +
          String(walletAddress).substring(38)
        ) : (
          <span>Connect Wallet</span>
        )}

      </button>
      <h2>Get all Partecipants:</h2>
      <button id="allPButton" onClick={getAllPatecipants}>Get All Partecipants</button>

      <p>List of Partecipant:</p>
      <ul>
        {ListPartecipants}
      </ul>

      <h2>Last Ticket Sold:</h2>
      <button id="lPButton" onClick={LastTicketSold}>Get Last Ticket Sold</button>

      <p>{LastPartecipant}</p>

      <h2>Number of Partecipants:</h2>
      <button id="lPButton" onClick={NumberOfPartecipants}>Get The number of Partecipants</button>
      <p>{NumPartecipant}</p>
            <h2>Buy Ticket:</h2>
            <p>Insert Name:</p>
            <input
                id="name"
                type="text"
                value={Name}
                required
                onChange={(e) => setName(e.target.value)}
            />
            <p>Insert Surname:</p>
            <input
                id="surname"
                type="text"
                value={Surname}
                required
                onChange={(e) => setSurname(e.target.value)}
            />
            <p>Insert Number of Tickets:</p>
            <input
                id="Ticket"
                type="number"
                max={99}
                min={1}
                value={Ticket}
                required
                onChange={(e) => setTicket(e.target.value)}
            />
            <button id="lPButton" onClick={buyTicket}>
                Sell Ticket
            </button>
            <p>{resultTransaction}</p>
            <button id="Extraction" onClick={Extraction}>Extract The Winner</button> 
            <button id="Extraction" onClick={showWinner}>Show Winner</button> 
            <p>{Winner}</p>
        </div>
    );
};

export default Lottery;
