import Web3 from 'web3';
const infuraKey = "https://sepolia.infura.io/v3/4af429facfe24c50ac278feda80bf2b2";
const web3 = new Web3(new Web3.providers.HttpProvider(infuraKey));
const contractABI = require("../contract-abi.json");
const contractAddress = "0xeBE029524cfa82341ddd8082d4d0f4Da7468c200";
const INST_METAMASK = "You must install Metamask in your browser."

export const ChristmasLotteryContract = new web3.eth.Contract(
    contractABI,
    contractAddress
  );
export const connectWallet = async () => {
  // checks if window.ethereum is installed in browser
  if (window.ethereum) {
      try {
          // try to connect to Metamask. Calling this function will open up Metamask in the browser
          const addressArray = await window.ethereum.request({
              method: "eth_requestAccounts",
          });
          const obj = {
              // take the first address in the array of addresses and display it to the user in our Wallet
              address: addressArray[0],
          };
          return obj;
          // if user denies access to their Metamask account, .request() will throw an error.
      } catch (err) {
          return {
              address: "",
              status: "ðŸ˜¥ " + err.message,
          };
      }
  } else {
      return {
          address: "",
          status: (
              <span>
                  <p>
                      {" "}
