                      <a target="_blank" href={`https://metamask.io/download.html`}>
                          {INST_METAMASK}
                      </a>
                  </p>
              </span>
          ),
      };
  }
};
export const getAllPartecipants = async (walletAddress) => {
  try {
      const message = await ChristmasLotteryContract.methods.GetAllPartecipants().call({from: walletAddress});
      let partecipants = [];
