          partecipants.push(message[i][1]+' '+message[i][2]);
      }
      console.log('message:', partecipants);   
      return {
          Partecipant: partecipants,
          status: "âœ… Success!"
      };

  }
  catch (error) {
      console.error('Error retrieving participants:', error);
      return {
          Partecipant: "Only the owner can see the participants",
          status: "Some error occurred while retrieving the participants ðŸ˜¥ "
      };
  }
};
export const GetLastTicketSold = async (walletAddress) => {
  try {
      const lastP = await ChristmasLotteryContract.methods.getLastTicketSold().call({from: walletAddress});
      console.log('message:', lastP[1]+''+lastP[2]);
      let last=[];
      last.push(lastP[1]+' '+lastP[2]);
      return {
          LastPartecipant: last,
          status: "âœ… Success!"
      };
  }
  catch (error) {
      console.error('Error retrieving participants:', error);
      return {
          LastPartecipant:"Only the owner can see the last participant" ,
          status: "Some error occurred while retrieving the Last participant ðŸ˜¥ "
      };
  }
};

export const getNumOfPartecipants = async (walletAddress) => {
  try {
      const np = await ChristmasLotteryContract.methods.GetNumOfPartecipants().call({from: walletAddress});
      let number=[];
      number.push(np);
      number[0]=number[0].toString();
      console.log('number:', number);
      return {
          NumPartecipant: number,
          status: "âœ… Success!"
      };
