  catch (error) {
      console.error('Error retrieving participants:', error);
      return {
          NumPartecipant: "Only the owner can see the number of participants",
          status: "Some error occurred while retrieving the Number of participant ðŸ˜¥ "
      };
  }
};

export const getCurrentWalletConnected = async () => {
  if (window.ethereum) {
    try {
      const addressArray = await window.ethereum.request({
        method: "eth_accounts",
      });
      if (addressArray.length > 0) {
        return {
          address: addressArray[0],
          status: "ðŸ‘†ðŸ½ Write a message in the text-field above.",
        };
      } else {
        return {
          address: "",
          status: "ðŸ¦Š Connect to Metamask using the top right button.",
        };
      }
    } catch (err) {
      return {
        address: "",
        status: "ðŸ˜¥ " + err.message,
      };
    }
  } else {
    return {
      address: "",
      status: INST_METAMASK
    };
  }
}
export const sellTicket = async (walletAddress,Name,Surname,Ticket) => {
  try {
      //set up transaction parameters
      const transactionParameters = {
          to: contractAddress, // Required except during contract publications.
          from: walletAddress, // must match user's active address.
          data: ChristmasLotteryContract.methods.SellTicket(Name,Surname,Ticket).encodeABI(),
      };
      //sign the transaction
